// add w to map, i.e. map[w]++
// 
// a hacky work-around: if w takes the form: images/xyz.ext, then we know it is an image.
function addWord(w, map) {
  if (!map.hasOwnProperty(w)) { // new word
    map[w] = 1;
  } else {
    map[w]++;
  }
}

// add value to map[key]
function addToList(key, value, map) {
  if(!map.hasOwnProperty(key)) {
    map[key] = [value];
  } else {
    map[key].push(value);
  }
}

// word <-> frequency
var words = {};
// come{After|Before}[w] => a list of words, which come after/before w
var comeAfter = {};
var comeBefore = {};

var loadFromServer = true;
if (loadFromServer) {
  loadDataFromServer("http://127.0.0.1:9000/load", loadCallBack);
} else { // initialize from hard-coded seedData above
  for (var i = 0; i < seedData.length; i++){
    addWord(seedData[i].first, words);
    addWord(seedData[i].second, words);
  }
  for (var i = 0; i < seedData.length; i++) {
    addToList(seedData[i].first, seedData[i].second, comeAfter);
    addToList(seedData[i].second, seedData[i].first, comeBefore);
  }
}

function showOne() {
  var w = document.getElementById("search").value;
  w = w.toLowerCase().trim();
  // clear old answer if any
  d3.selectAll(".answer").remove();
  d3.select("#tooltip").classed("hidden", true);
  if (w === "" || !words.hasOwnProperty(w)) {
    d3.select("body").append("div")
      .attr("class", "answer")
      .text("You don't have any note for " + "\"" + w + "\" yet!");
  } else {
    makeGraph(w);
  }
}

// when clicking on a node
function showSelected(word) {
  document.getElementById("search").value = word;
  d3.select(".answer").remove();
  d3.select("#tooltip").classed("hidden", true);
  makeGraph(word);
}

function makeGraph(word) {
  // The size of svg
  var W = window.innerWidth;
  var H = window.innerHeight;

  var svg = d3.select("body").append("svg")
    .attr("width", W)
    .attr("height", H)
    .attr("class", "answer")

  var force = d3.layout.force()
    //.charge(-80)
    .linkDistance(200)
    .size([W, H]);

  var gNodeData = [{name: word, freq: words[word]}];
  var gLinkData = [];

  var afterLen = comeAfter.hasOwnProperty(word) ? comeAfter[word].length : 0;
  for (var i = 0; i < afterLen; i++) {
    gNodeData.push({name: comeAfter[word][i], freq: words[comeAfter[word][i]]});
    // source/target index should be index of gNodeData array, not wIdx
    gLinkData.push({source: 0, target: i+1, after: true});
  }
  var beforeLen = comeBefore.hasOwnProperty(word) ? comeBefore[word].length : 0;
  for (var i = afterLen; i < afterLen+beforeLen; i++) {
    gNodeData.push({name: comeBefore[word][i-afterLen], freq: words[comeBefore[word][i-afterLen]]});
    gLinkData.push({source: 0, target: i+1, after: false});
  }

  // data binding
  force.nodes(gNodeData)
       .charge(function(d){
          return d.freq * d.freq * -10;
       })
       .links(gLinkData)
       .start();

  var links = svg.selectAll(".link")
    .data(gLinkData)
    .enter()
    .append("line")
    .attr("class", "link")
    .style("stroke", function(d) {
      if (d.after) {
        return "blue"; // color after-link with blue
      }
      return "red"; // otherwise, red
    })
    .style("stroke-width", 2);

  var colors = d3.scale.category20();
  var nodes = svg.selectAll(".node")
    .data(gNodeData)
    .enter()
    .append(function(d) {
    	if(d.name.startsWith("images/")) {
    	    return document.createElementNS("http://www.w3.org/2000/svg", "svg:image");
    	} else return document.createElementNS("http://www.w3.org/2000/svg", "svg:circle");
    })
    .attr("class", "node")
    .attr("r", function(d){ 
	if(!d.name.startsWith("images/")) 
	    return d.freq; 
	return 0;
    })
    // .attr("xlink:href", function(d){
    // 	if(d.name.startsWith("images/")) return d.name;
    // 	return "";
    // })
    // .attr("width", function(d){
    // 	if(d.name.startsWith("images/")) return 50;
    // 	return 0;
    // })
    // .attr("height", function(d){
    // 	if(d.name.startsWith("images/")) return 50;
    // 	return 0;
    // })
    .style("fill", function(d){ return colors(d.freq); })
    //.on("click", function(d){ showSelected(d.name); })
    .call(force.drag); // enable dragging effect

    var images = nodes.filter(function(d){
       return d.name.startsWith("images/");
    })
    .attr("xlink:href", function(d){
    	console.log(d.name);
    	return d.name;
    })
    .attr("width", 50)
    .attr("height", 50)
    .on("click", function(d) {
      // don't create a new graph when click on the root word
      if(d.name === word) return;
      showSelected(d.name);
    })
    .call(force.drag);

  var labels = svg.selectAll(".label")
    .data(gNodeData)
    .enter()
    .append("text")    
    .attr("class", "label")
    .attr("text-anchor", "middle")
    .style("font-family", "sans-serif")
    .style("font-size", "13")
    .text(function(d){ 
	if(d.name.startsWith("images/")) return "";
	return d.name; 
    })
    .on("click", function(d) {
      // don't create a new graph when click on the root word
      if(d.name === word) return;
      showSelected(d.name);
    })
    .on("mouseover", function(d) {
      if(d.name.startsWith("images/")) return;

      var xPos = d.x;
      var yPos = d.y;
      var note = [];
      if(d.name === word) {
        note.push("It's me: " + d.name);
      }
      if(comeAfter.hasOwnProperty(word) && comeAfter[word].includes(d.name)) {
        note.push(word + " " + d.name);
      }
      // If there is a word that can both come before and after another word
      if(comeBefore.hasOwnProperty(word) && comeBefore[word].includes(d.name)) {
        note.push(d.name + " " + word);
      }
      d3.select("#tooltip")
        .style("left", xPos+"px")
        .style("top", yPos+"px")
        .select("#story")
        .text(note.join("\n"));
      // Show the tooltip
      d3.select("#tooltip").classed("hidden", false);
    })
    .on("mouseout", function(){
      d3.select("#tooltip").classed("hidden", true);
    })
    .call(force.drag);

  // force layout automatically computes x/y values for links, nodes, labels
  force.on("tick", function(){
    links.attr("x1", function(d){ return d.source.x; })
         .attr("y1", function(d){ return d.source.y; })
         .attr("x2", function(d){ return d.target.x; })
         .attr("y2", function(d){ return d.target.y; });

    nodes.attr("cx", function(d){ return d.x; })
         .attr("cy", function(d){ return d.y; });

    labels.attr("x", function(d){ return d.x; })
          .attr("y", function(d){ return d.y; });

    images.attr("x", function(d){ return d.x; })
	  .attr("y", function(d){ return d.y; });

  });
}

function addCollocation() {
  var first = document.getElementById("first").value;
  var second = document.getElementById("second").value;
  first = first.toLowerCase().trim();
  second = second.toLowerCase().trim();

  addWord(first, words);
  addWord(second, words);
  // avoid duplicate links
  // TODO(D): Alternatively, consider use link frequency as edge width?
  if (!comeAfter.hasOwnProperty(first) || !comeAfter[first].includes(second)){
    addToList(first, second, comeAfter);
  }
  if (!comeBefore.hasOwnProperty(second) || !comeBefore[second].includes(first)) {
    addToList(second, first, comeBefore);
  }
  document.getElementById("first").value = "";
  document.getElementById("second").value = "";
}

// TODO(d): Use window.setInterval to save data to server periodically in
// addition to wating for user pressing Done button
function saveDataToServer(url, data, callback) {
  var request = new XMLHttpRequest();
  request.open("POST", url); // default to asynchronous request
  request.onreadystatechange = function() {
    if(request.readyState === 4 && callback) {
      callback(request);
    }
  };
  request.setRequestHeader("Content-Type", "application/json");
  request.send(JSON.stringify(data));
}

function saveCallBack(req) {
  // remove old status if any
  d3.selectAll(".status").remove();
  d3.select("body").append("div")
    .attr("class", ".status")
    .text("[Save data] " + req.status + ": " + req.statusText);
}

// saveHandler is called when user pushes the "Save" button
function saveHandler() {
  data = {"words": words, "comeAfter": comeAfter, "comeBefore": comeBefore};
  saveDataToServer("http://127.0.0.1:9000/save", data, saveCallBack);
}

function loadDataFromServer(url, callback) {
  var request = new XMLHttpRequest();
  request.open("GET", url);
  request.onreadystatechange = function() {
    if(request.readyState === 4 && request.status === 200) {
      var responseType = request.getResponseHeader("Content-Type");
      //console.log(responseType);
      if(responseType === "application/json") {
        //console.log(request.responseText);
        callback(JSON.parse(request.responseText));
      }
    }
  };
  request.send(null); // GET request has no body, just send NULL
}

function loadCallBack(data) {
  // Populate words, comeAfter, comeBefore
  words = data["words"];
  comeAfter = data["comeAfter"];
  comeBefore = data["comeBefore"];
}
